def build_appinfochanges_response(client_obj, last_change_number, do_fullupdate):
    """Constructs the server response for AppInfo changes based on the client's request.

    :param client_obj: The client object associated with the request.
    :param last_change_number: The last change number known to the server.
    :param do_fullupdate: Boolean indicating whether a full update is requested.
    :param app_data_list: List of tuples containing (AppID, ChangeNumber, SectionData) for each application.
    :return: A properly structured packet for AppInfoChanges.
    """

    # FIXME: this is for 2009-april 2010 clients.  the packet is changed after april 2010!!!!
    current_cdr_date = datetime.strptime(globalvars.CDDB_datetime, "%m/%d/%Y %H:%M:%S")
    current_changeid = create_4byte_id_from_date(current_cdr_date)
    if int(last_change_number).to_bytes(4, "little") != current_changeid: # FIXME we ignore the last change number because we insert our own based on the CDDB date
        found_appinfo_list = find_appids_by_date(globalvars.CDDB_datetime)
    else:
        packet = CMResponse(eMsgID=EMsg.ClientAppInfoChanges, client_obj=client_obj)
        msg = MsgClientAppInfoChanges()
        msg.current_change_number = last_change_number
        msg.force_full_update = 0
        msg.app_ids = []
        buffer = msg.serialize()
        packet.data = bytes(buffer)
        return packet

    # Sort the found_appinfo_list by AppID (second element in the tuple)
    # Create response packet
    packet = CMResponse(eMsgID=EMsg.ClientAppInfoChanges, client_obj=client_obj)

    msg = MsgClientAppInfoChanges()
    msg.current_change_number = int().from_bytes(current_changeid, "little")
    msg.force_full_update = do_fullupdate

    # Populate app IDs from app_data_list
    # Populate app IDs into the message
    msg.app_ids = [7, 440]#found_appinfo_list

    # Serialize the message to create the buffer
    buffer = msg.serialize()

    # Assign the constructed buffer to the packet's data
    packet.data = bytes(buffer)

    return packet

